import logo from './logo.svg';
import './App.css';
import Control from './components/Control.js'
import ListStudent from './components/ListStudent.js'
import Form from './components/Form.js';
import React, { createContext, useState, useEffect } from 'react';


export const StudentContext = createContext()
function App() {
  const [listAllStudent, setListAllStudent] = useState([])
  const [isForm, setIsForm] = useState(false)
  const [actionName, setActionName] = useState('')

  useEffect(() => {
    let listAllStudent = JSON.parse(localStorage.getItem('listAllStudent'))
    if (listAllStudent) {
      setListAllStudent(listAllStudent)
    }
  }, [])

  useEffect(() => {
    localStorage.setItem('listAllStudent', JSON.stringify(listAllStudent));
  }, [listAllStudent])

  let elementForm;
  if (isForm) {
    elementForm = <Form></Form>
  }
  else {
    elementForm = ''
  }

  const isToggleAction = (formStatus, actionName) => {
    setIsForm(formStatus);
    setActionName(actionName);
  }

  const setNewStudent = (newStudent) => {
    console.log(newStudent);
    setListAllStudent([...listAllStudent, newStudent])
  }

  return (
    <StudentContext.Provider value={{ isToggleAction: isToggleAction, setNewStudent: setNewStudent, actionName: actionName, listAllStudent: listAllStudent }}>
      <div className="App">
        <div className="row">
          <div className="col-lg-7 grid-margin stretch-card">
            <div className="card">
              {/* START CONTROL */}
              <Control></Control>
              {/* END CONTROL */}
              {/* START LIST STUDENT */}
              <ListStudent></ListStudent>
              {/* END LIST STUDENT */}
            </div>
          </div>
          {/* START FORM SINH VIEN */}
          {elementForm}
          {/* END FORM SINH VIÃŠN */}
        </div>
      </div>
    </StudentContext.Provider>
  );
}

export default App;
