import React, { Component, useContext, useEffect, useReducer } from "react";
import { StudentContext } from "../App";


const VIEW = 'view'
const EDIT = 'edit'
const DELETE = 'delete'
const ReducerStudent = (state, action) => {
    // let listContext = useContext(StudentContext)
    switch (action) {
        case VIEW:
            console.log("view");
            return state = { status: "view" };
        case EDIT:
            console.log("edit");
            return state = "edit";
        case DELETE:
            return state = "delete";
    }
}
function Student(props) {
    const [status, dispatch] = useReducer(ReducerStudent, { status: '', log: 0 })
    // console.log(status);
    let { student } = props;
    let listContext = useContext(StudentContext)
    useEffect(() => {
        console.log("effect");
        switch (status.status) {
            case "view":
                console.log("view");
                listContext.isToggleAction(true, "", student);
                break;
            case "edit":
                console.log("edit");
                listContext.isToggleAction(true, "UpdateStudent", student);
                break;
            case "delete":
                console.log("delete");
                break;
            default:
                console.log("Invalid");
        }
    }, [status])

    // const viewStudent = () => {
    //     listContext.isToggleAction(true, "", student);
    // }

    // const editStudent = () => {
    //     listContext.isToggleAction(true, "UpdateStudent", student);
    // }

    // const deleteStudent = () => {
    //     let checkconfirm = window.confirm("Bạn có chắc chắn muốn xóa Sinh viên này không?");
    //     if (!checkconfirm) {
    //         return;
    //     }
    //     listContext.deleteStudent(student)
    // }

    return (
        <tr>
            <td>{props.stt + 1}</td>
            <td>{student.studentId}</td>
            <td>{student.studentName}</td>
            <td>{student.studentAge}</td>
            <td>{student.studentSex.toString() == "true" ? "Nam" : (student.studentSex.toString() == "false" ? "Nữ" : "Khác")}</td>
            <td>
                <div className="template-demo">
                    <button
                        type="button"
                        className="btn btn-danger btn-icon-text"
                        onClick={() => { dispatch(VIEW) }}
                    // onClick={viewStudent}
                    >
                        Xem
                    </button>
                    <button
                        type="button"
                        className="btn btn-warning btn-icon-text"
                        onClick={() => { dispatch(EDIT) }}
                    // onClick={editStudent}
                    >
                        Sửa
                    </button>
                    <button
                        type="button"
                        className="btn btn-success btn-icon-text"
                        onClick={() => { dispatch(DELETE) }}
                    // onClick={deleteStudent}
                    >
                        Xóa
                    </button>
                </div>
            </td>
        </tr>
    )
}

export default Student
