    elementForm = <Form></Form>
  }
  else {
    elementForm = ''
  }

  return (
    <div className="App">
      <div className="row">
        <div className="col-lg-7 grid-margin stretch-card">
          <div className="card">
            {/* START CONTROL */}
            <Control></Control>
            {/* END CONTROL */}
            {/* START LIST STUDENT */}
            <ListStudent></ListStudent>
            {/* END LIST STUDENT */}
          </div>
        </div>
        {/* START FORM SINH VIEN */}
        {elementForm}
        {/* END FORM SINH VIÃŠN */}
      </div>
    </div>
  );
}

export default App;
