import React, { useContext, useState } from "react";
import { StudentContext } from "../App";


function Form() {
    const [studentInfo, setStudentInfo] = useState({
        studentId: '',
        studentName: '',
        studentAge: '',
        studentSex: '',
        studentDateBirth: '',
        studentPlaceBirth: '',
        studentAddress: '',
    });

    const [studentId, setStudentId] = useState('');
    const [studentName, setStudentName] = useState('');
    const [studentAge, setStudentAge] = useState('');
    const [studentSex, setStudentSex] = useState('');
    const [studentDateBirth, setStudentDateBirth] = useState('');
    const [studentPlaceBirth, setStudentPlaceBirth] = useState('');
    const [studentAddress, setStudentAddress] = useState('');


    let listContext = useContext(StudentContext)
    let elementBtn = ''

    const getStudentInfo = (event) => {
        let value = event.target.value;
        let name = event.target.name;
        console.log(value);
        switch (name) {
            case "studentId":
                setStudentId(value)
                break;
            case "studentName":
                setStudentName(value)
                break;
            case "studentAge":
                setStudentAge(value)
                break;
            case "studentSex":
                setStudentSex(value)
                break;
            case "studentDateBirth":
                setStudentDateBirth(value)
                break;
            case "studentPlaceBirth":
                setStudentPlaceBirth(value)
                break;
            case "studentAddress":
                setStudentAddress(value)
                break;
        }
    }

    const setValueStudentInfo = () => {
        setStudentInfo({
            studentId: studentId,
            studentName: studentName,
            studentAge: studentAge,
            studentSex: studentSex,
            studentDateBirth: studentDateBirth,
            studentPlaceBirth: studentPlaceBirth,
            studentAddress: studentAddress,
        })
        return studentInfo;
    }

    const setNewStudent = (event) => {
        event.preventDefault();
        listContext.setNewStudent(() => {
        })

    }

    if (listContext.actionName == "CreateStudent") {
        elementBtn = <button type="submit" className="btn btn-primary me-2" onClick={setNewStudent}> Create</button>
    }
    else if (listContext.actionName == "UpdateStudent") {
        elementBtn = <button type="submit" className="btn btn-primary me-2"> Update</button>
    }

    return (
        <div className="col-5 grid-margin">
            <div className="card">
                <div className="card-body">
                    <h3 className="card-title">Thông tin sinh viên</h3>
                    <i className="fa-regular fa-circle-xmark fa-xl icon-close" />
                    <form className="form-sample">
                        <div className="form-group row">
                            <label className="col-sm-3 col-form-label">Mã sinh viên</label>
                            <div className="col-sm-9">
                                <input type="text" className="form-control" name="studentId" onChange={getStudentInfo} />
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-3 col-form-label">Tên sinh viên</label>
                            <div className="col-sm-9">
                                <input type="text" className="form-control" name="studentName" onChange={getStudentInfo} />
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-3 col-form-label">Tuổi</label>
                            <div className="col-sm-9">
                                <input type={"number"} className="form-control" name="studentAge" onChange={getStudentInfo} />
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-3 col-form-label">Giới tính</label>
                            <div className="col-sm-9">
                                <select className="form-control" name="studentSex" onChange={getStudentInfo}>
                                    <option value={''}>Khác</option>
                                    <option value={true}>Nam</option>
                                    <option value={false}>Nữ</option>
                                </select>
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-3 col-form-label">Ngày sinh</label>
                            <div className="col-sm-9">
                                <input type={"date"} className="form-control" placeholder="dd/mm/yyyy" name="studentDateBirth" onChange={getStudentInfo} />
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-3 col-form-label">Nơi sinh</label>
                            <div className="col-sm-9">
                                <select className="form-control" name="studentPlaceBirth" onChange={getStudentInfo}>
                                    <option value={"select"}>Lựa chọn</option>
                                    <option value={"Hà Nội"}>Hà Nội</option>
                                    <option value={"TP. Hồ Chí Minh"}>TP. Hồ Chí Minh</option>
                                    <option value={"Đà Nẵng"}>Đà Nẵng</option>
                                    <option value={"Quảng Ninh"}>Quảng Ninh</option>
                                </select>
                            </div>
                        </div>
                        <div className="form-group row">
                            <label className="col-sm-3 col-form-label">Địa chỉ</label>
                            <div className="col-sm-9">
                                <textarea className="form-control" name="studentAddress" onChange={getStudentInfo} />
                            </div>
                        </div>
                        {/* Submit */}
                        {elementBtn}
                    </form>
                </div>
            </div >
        </div >
    )
}
export default Form
